mapper.select:
select
  oper_id, title, business_type, method, request_method, oper_name, oper_url, oper_ip, status, errorMsg, operTime
from hqh_oper_log


        and oper_id = #{hqhOperLog.operId}


        and title = #{hqhOperLog.title}


        and business_type = #{hqhOperLog.businessType}


        and method = #{hqhOperLog.method}


        and request_method = #{hqhOperLog.requestMethod}


        and oper_name = #{hqhOperLog.operName}


        and oper_url = #{hqhOperLog.operUrl}


        and oper_ip = #{hqhOperLog.operIp}


        and status = #{hqhOperLog.status}


        and errorMsg = #{hqhOperLog.errormsg}


        and operTime = #{hqhOperLog.opertime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from hqh_oper_log


        and oper_id = #{operId}


        and title = #{title}


        and business_type = #{businessType}


        and method = #{method}


        and request_method = #{requestMethod}


        and oper_name = #{operName}


        and oper_url = #{operUrl}


        and oper_ip = #{operIp}


        and status = #{status}


        and errorMsg = #{errormsg}


        and operTime = #{opertime}
